import { Component, triggerCatchEvent, triggerEventOnly, } from '../../_util/simply';
Component({
    props: {
        image: '',
        title: '',
        brief: '',
        arrow: false,
        extra: '',
        extraBrief: '',
        disabled: false,
        showDivider: true,
    },
    methods: {
        onTap: function (e) {
            if (this.props.disabled) {
                return;
            }
            triggerEventOnly(this, 'tap', e);
        },
        catchTap: function (e) {
            if (this.props.disabled) {
                return;
            }
            triggerCatchEvent(this, 'catchTap', e);
        },
    },
});
